import React from 'react'

export default class ArticleTitleBox extends React.Component {
  state = {
    articleTitle : ''
  }

  componentWillUpdate(nextProps, nextState) {
    this.setState({
      articleTitle : this.props.title
    })
  }

  render(){
    const {title} = this.props;
    return(
      <React.Fragment>
        {this.state.articleTitle}
      </React.Fragment>

    )
  }
}
