import React,{ Component } from 'react'
import styled from 'styled-components'
import { Link } from 'react-router-dom';
import HorizontalScroll from './horizontalScroll'
import workData from '../work';

export default class Articles extends Component {
  render() {
    console.log(workData.results);
    return (
      <HorizontalScroll>
        <ArticleBox className='articles'>
          <li>
            <div>
              <p>안녕하세요.</p>
              <p>UI / UX 디자이너 <strong>김재현</strong> 입니다.</p>
              <p>저의 <strong>포트폴리오</strong>에 오신것을 환영합니다.</p>
            </div>
          </li>
          {workData.results.map((article) => (
            <li key={article.id}>
              <Link to={{
                pathname : `post/${article.id}`,
                state : `${article.content}`,
              }}>
                <img src={article.img} alt={article.title}/>
              </Link>
            </li>
          ))}
        </ArticleBox>
      </HorizontalScroll>
    );
  }
}

const ArticleBox = styled.ul`
  padding:0;
  margin:0;
  list-style:none;
  display:flex;
  top: 8.898895vw;
  bottom: 5.26593vw;
  position:absolute;
  overflow:hidden;
  >li{
    padding:0 5.26593vw
    display: flex;
    &:nth-child(1n){
      align-items: center;
    }
    &:nth-child(2n){
      align-items: flex-start;
    }
    &:nth-child(3n){
      align-items: flex-end;
    }
    &:first-child{
      align-items: flex-start;
      width:340px;
      >div{
        padding-top:5.26593vw;
        > p{
          font-size: 1.3125rem;
          line-height: 1.4em;
          margin: 0 0.2em;
          font-weight: 200;
        }
      }
    }
    a > img{
      max-width:600px;
    }
  }
`
